  url, sort, pid, type, 
  create_user, create_time, 
  last_modified_user, last_modified_time, remark
  )
values (#{id,jdbcType=BIGINT}, #{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{url,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}, #{pid,jdbcType=BIGINT}, #{type,jdbcType=INTEGER}, 
  #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{lastModifiedUser,jdbcType=VARCHAR}, #{lastModifiedTime,jdbcType=TIMESTAMP}, #{remark,jdbcType=VARCHAR}
  )
